$(document).ready(function(){$(".scroll-top").click(function(){$("body,html").animate({scrollTop:0},1E3)});$("body").scrollspy({target:"#navbar"});window.onresize=function(){$("canvas").width($(window).width())};$(document).bind("touchmove",function(){$("canvas").css("-webkit-transform","translatey(-"+$(window).scrollTop()+"px)")});$(document).bind("touchend",function(){$("canvas").css("-webkit-transform","translatey(-"+$(window).scrollTop()+"px)")})});
